<template>
<div>
  <b-navbar>
    <template #brand>
      <b-navbar-item tag="router-link" :to="{ path: '/register' }">
        <img
                    src="data:image/png;base64,"
                    alt="Lightweight UI components for Vue.js based on Bulma"
                >
        </b-navbar-item>
      </template>
    </b-navbar>

    <div class="columns" style="justify-content: center">
      <div class="column is-half">
        <template>
          <h1 class="is-size-2">Bienvenido</h1>
          <section>
            <b-field label="Nombre">
              <b-input v-model="userData.name"></b-input>
            </b-field>

            <b-field label="Apellido">
              <b-input v-model="userData.surname"></b-input>
            </b-field>

            <b-field label="Email">
              <b-input type="email"
                maxlength="40"
                v-model="userData.email">
              </b-input>
            </b-field>

            <b-field label="Contraseña">
              <b-input type="password"
                v-model="userData.password"
                password-reveal>
              </b-input>
            </b-field>

            <div v-if="formError !== ''">
              <b-notification
                type="is-danger"
                has-icon
                aria-close-label="Close notification"
                role="alert">
                {{ formError }}
              </b-notification>
            </div>

            <div class="buttons">
              <b-button type="is-primary" native-type="submit" expanded @click.prevent="createAccount">Registrarse</b-button>
            </div>

          </section>
        </template>
        <footer>
          <h3>Al registrarme, acepto los <a @click="goToTermsPage">Términos y Condiciones de Pulpo</a></h3>
        </footer>
      </div>
    </div>
  </div>
</template>

<script>

import { Auth, UsersRef } from "@/modules/firebase"


export default {
  name: 'EmailSignup',
  data() {
    return{
      formError: "",
      userData: {
        name: "",
        surname: "",
        email: "",
        password: "",
        role: "user",
        active: true
      },
    };
  },
  methods: {
     async addUser(uid){
 
      const userInfo = {
        name: this.userData.name,
        surname: this.userData.surname,
        email: this.userData.email,
        password: this.userData.password,
        created_at: new Date(),
        role: this.userData.role,
        active: this.userData.active
      };

      console.info(userInfo)

      try{
        const result = await UsersRef.doc(uid).set(userInfo)
        
        console.info(result);
      } catch(e){
        console.error(e)
      } 
    },
    async createAccount() {
      this.formError = "";

      if (this.userData.email === "") {
        this.formError = "Debes introducir tu email";
        return;
      }

      if (this.userData.password.length < 6) {
        this.formError = "La contraseña debe tener al menos 6 caracteres.";
        return;
      }

      try {
        const result = await Auth.createUserWithEmailAndPassword(
          this.userData.email,
          this.userData.password
        );
        
        console.log(result)

        this.addUser(result.user.uid)

        this.$router.push('/')

      } catch (e) {
        this.formError = e.message;
      }
    },
    goToTermsPage() {
      this.$router.push("/terms");
    },
  },
}
</script>

<style scoped>
h1, h3{
  text-align: center;
}

h3{
  margin-top: 10px;
}
</style>